package adventofcode.y2022.day6;

public class TuningTrouble {

    public static void main(String[] args) {
        String s = "";
        int c1=-1;
        int c2=-1;
        int c3=-1;
        int c4=-1;
        for (int i = 0;i<s.length();i++){
            c1=c2;
            c2=c3;
            c3=c4;
            c4=s.charAt(i);
            if(c1!=c2&&c1!=c3&&c1!=c4&&c2!=c3&&c2!=c4&&c3!=c4&&c1!=-1) {
                System.out.println(i + 1);
                break;
            }
        }
    }
}
